import { createElement } from "lwc";
import Block from "c/block";

describe("c-block", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  /* Check if the element renders */
  it("renders DOM elements", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block";
    element.theme = "slds";
    element.collapsible = "true";
    element.collapsedByDefault = "false";
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Has theme set", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block";
    element.theme = "slds";
    element.collapsible = "true";
    element.collapsedByDefault = "false";
    document.body.appendChild(element);
    const block = element.shadowRoot.querySelectorAll("div")[1];
    let index = block.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /**
   * Check if the Label is set properly
   */
  it("Has Label set", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block Label";
    element.theme = "slds";
    element.collapsible = "true";
    element.collapsedByDefault = "false";
    document.body.appendChild(element);
    const block = element.shadowRoot.querySelector(".slds-text-heading_small");
    const blockHeader = element.shadowRoot.querySelector(".block-header");
    expect(blockHeader).not.toBe(undefined);
    expect(block.textContent).toBe("Block Label");
  });

  /**
   * Check if the collapsible is set properly
   */
  it("Has collapsible set", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block Label";
    element.theme = "slds";
    element.collapsible = false;
    element.collapsedByDefault = "false";
    document.body.appendChild(element);
    const block = element.shadowRoot.querySelector(".block-header");
    expect(block).toBe(null);
  });

  /**
   * Check if the collapsedByDefault is set properly
   */
  it("Has collapsedByDefault set", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block Label";
    element.theme = "slds";
    element.collapsible = true;
    element.collapsedByDefault = true;
    document.body.appendChild(element);
    const block = element.shadowRoot.querySelector(".blockSlot");
    expect(block).not.toBe(null);
  });

  /**
   * Sets aria-label attribute only if block has label set to some value
   */
  it("Has ariaLabel set, if label value is present", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block Label";
    element.theme = "slds";
    element.collapsible = true;
    element.collapsedByDefault = true;
    document.body.appendChild(element);
    const blockLabel = element.shadowRoot.querySelector(
      ".data-interpolatedLabel"
    );
    const ariaLabel = blockLabel.getAttribute("aria-label");
    expect(ariaLabel).not.toBe(null);
    expect(ariaLabel).toBe("Block Label");
  });

  /**
   * Does not ser aria-label attribute if block does not has label set to some value
   */
  it("will not set ariaLabel, if label value is not set", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "";
    element.theme = "slds";
    element.collapsible = true;
    element.collapsedByDefault = true;
    document.body.appendChild(element);
    const blockLabel = element.shadowRoot.querySelector(
      ".data-interpolatedLabel"
    );
    const ariaLabel = blockLabel.getAttribute("aria-label");
    expect(ariaLabel).toBe(null);
  });

  /**
   * Sets aria-label attribute only if block has label set to some value
   */
  it("Has ariaLabel set, if label value is present, when collapsible is disbaled", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "Block Label";
    element.theme = "slds";
    element.collapsible = false;
    element.collapsedByDefault = true;
    document.body.appendChild(element);
    const blockLabel = element.shadowRoot.querySelector(
      ".data-interpolatedLabel"
    );
    const ariaLabel = blockLabel.getAttribute("aria-label");
    expect(ariaLabel).not.toBe(null);
    expect(ariaLabel).toBe("Block Label");
  });

  /**
   * Does not set aria-label attribute if block does not has label set to some value
   */
  it("will not set ariaLabel, if label value is not set, when collapsible is disbaled", () => {
    const element = createElement("c-block", {
      is: Block
    });
    element.label = "";
    element.theme = "slds";
    element.collapsible = false;
    element.collapsedByDefault = true;
    document.body.appendChild(element);
    const blockLabel = element.shadowRoot.querySelector(
      ".data-interpolatedLabel"
    );
    const ariaLabel = blockLabel.getAttribute("aria-label");
    expect(ariaLabel).toBe(null);
  });
});
