import { isOmniStudio, isOmnistudioPkg } from "c/flexCardUtils";
import { localStoreMethods } from "c/flexCardUtility";

export function isFoundationPkg() {
  if (localStoreMethods.getItem("omniStudio.isFoundationPkg")) {
    return new Promise(function(resolve) {
      resolve(
        JSON.parse(localStoreMethods.getItem("omniStudio.isFoundationPkg"))
      );
    });
  }
  return isOmniStudio().then(data => {
    localStoreMethods.setItem(
      "omniStudio.isFoundationPkg",
      JSON.stringify(data)
    );
    return data;
  });
}

export function isOmnistudioPackage() {
  if (localStoreMethods.getItem("omniStudio.isOmnistudioPkg")) {
    return new Promise(function(resolve) {
      resolve(
        JSON.parse(localStoreMethods.getItem("omniStudio.isOmnistudioPkg"))
      );
    });
  }
  return isOmnistudioPkg().then(data => {
    localStoreMethods.setItem(
      "omniStudio.isOmnistudioPkg",
      JSON.stringify(data)
    );
    return data;
  });
}
