import { createElement } from "lwc";
import MaskedInput from "c/maskedInput";
import { delay } from "c/asyncUtils";

describe("c-masked-input", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: Number(),
      thousandsSeparator: ",",
      radix: "."
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "123456";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    expect(element.shadowRoot.children).not.toHaveLength(0);
  });

  /**
   * Check if the theme is applied properly
   */
  it("Has theme set", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: Number(),
      thousandsSeparator: ",",
      radix: "."
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "123456";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    const input = element.shadowRoot.querySelector("div");
    let index = input.className.indexOf("slds") !== -1 ? true : false;
    expect(index).toBe(true);
  });

  /**
   * Check if the value is set
   */
  it("Has value is set", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: Number(),
      thousandsSeparator: ",",
      radix: ".",
      numberMask: true
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "123456";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    const input = element.shadowRoot.querySelector("input");
    expect(element.value).toBe(123456);
    expect(element.maskedValue).toBe("123,456");
    expect(input.value).toBe("123,456");
  });

  /**
   * Check if the mask value is set with placeholderChar
   */
  it("Has mask value is set with placeholderChar", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      placeholderChar: "#" // defaults to '_'
    };
    element.showMaskWhen = "ALWAYS"; // make placeholder always visible
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    const input = element.shadowRoot.querySelector("input");
    expect(input.value).toBe("+7(###)###-##-##");
  });

  /**
   * Check if the masked value works
   */
  it("Check if the masked value works", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    expect(element.maskedValue).toBe("+7(123)456-78-90");
  });

  /**
   * Check if the unmasked value works
   */
  it("Check if the unmasked value works", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    expect(element.value).toBe("71234567890");
  });

  /**
   * Check if the typedValue is set
   */
  it("Check if the typedValue is set", () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: Number(),
      thousandsSeparator: ",",
      radix: ".",
      numberMask: true
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = 100;
    element.type = "number";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    expect(element.typedValue).toBe(100);
  });

  /**
   * Check if currency validation works
   */
  it("Check if the currency validation works", () => {
    const element = createElement("runtime_omnistudio_common-masked-input", {
      is: MaskedInput
    });
    element.label = "InputMask";
    element.theme = "slds";
    element.type = "currency";
    document.body.appendChild(element);

    const ele = element.shadowRoot.querySelector("input");
    return Promise.resolve().then(() => {
      element.value = "abc1122";
      ele.dispatchEvent(new CustomEvent("input"));
      expect(element.value).toBe(1122);
    });
  });

  /**
   * Check for custom required validation
   */
  it("Check if custom required validation works.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: "(aaa)"
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "text";
    element.required = true;
    element.imask = maskAttrib;
    document.body.appendChild(element);

    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = "abc";
    expect(element.reportValidity()).toBe(true);
  });

  it("Check if custom is complete validation works.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: "(aaa)"
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "text";
    element.required = true;
    element.imask = maskAttrib;
    document.body.appendChild(element);
    element.value = "ab";
    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = "abc";
    expect(element.reportValidity()).toBe(true);
  });

  /**
   * Check for custom required validation for number type
   */
  it("Check if custom required validation works for number type.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "number";
    element.required = true;
    element.imask = maskAttrib;
    document.body.appendChild(element);

    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = 0;
    expect(element.reportValidity()).toBe(true);
  });

  it("Check if custom validation works number, min value.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "number";
    element.imask = maskAttrib;
    element.min = 10;
    document.body.appendChild(element);

    element.value = 1;
    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = 10;
    expect(element.reportValidity()).toBe(true);
  });

  it("Check if custom validation works number, max value.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "number";
    element.imask = maskAttrib;
    element.max = 10;
    document.body.appendChild(element);

    element.value = 11;
    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = 10;
    expect(element.reportValidity()).toBe(true);
  });

  it("Check if custom validation works for exceeding max safe int.", () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "number";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    element.value = Number.MAX_SAFE_INTEGER + 1;
    expect(element.checkValidity()).toBe(false);
    expect(element.validity.valid).toBe(false);
    element.value = 0;
    expect(element.reportValidity()).toBe(true);
  });

  it("Check if the styles get applied with masked element for label [LIGHTNING]", async () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    element.styles =
      '{"label":{"color":"#E4CC70","textAlign":"center","fontSize":"34px","textDecoration":"line-through","fontFamily":"cursive"},"value":{"color":"#E6EDE8","textAlign":"center","fontSize":"24px","backgroundColor":"#2A45DD","textDecoration":"line-through","fontFamily":"cursive"}}';

    document.body.appendChild(element);
    expect(element.maskedValue).toBe("+7(123)456-78-90");

    await delay(10);

    const label = element.shadowRoot.querySelector(
      "label.slds-form-element__label.slds-show--inline span"
    );
    await delay(10);
    expect(label.style.color).toBe("rgb(228, 204, 112)"); // hex code: #E4CC70;
    expect(label.style.fontSize).toBe("34px");
    expect(label.style.textDecoration).toBe("line-through");
    expect(label.style.fontFamily).toBe("cursive");

    // For label textAlign doesn't support
    expect(label.style.textAlign).toBe("");
  });

  it("Check if the styles get applied with masked element for input value [LIGHTNING]", async () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "slds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    element.styles =
      '{"label":{"color":"#E4CC70","textAlign":"center","fontSize":"34px","textDecoration":"line-through","fontFamily":"cursive"},"value":{"color":"#E6EDE8","textAlign":"center","fontSize":"24px","backgroundColor":"#2A45DD","textDecoration":"line-through","fontFamily":"cursive"}}';

    document.body.appendChild(element);
    expect(element.maskedValue).toBe("+7(123)456-78-90");

    await delay(10);

    const input = element.shadowRoot.querySelector(
      "input.vlocity-input.slds-input"
    );
    await delay(10);
    expect(input.style.color).toBe("rgb(230, 237, 232)"); // hex code: #E6EDE8;
    expect(input.style.fontSize).toBe("24px");
    expect(input.style.textAlign).toBe("center");
    expect(input.style.backgroundColor).toBe("rgb(42, 69, 221)"); // hex code: #2A45DD;
    expect(input.style.textDecoration).toBe("line-through");
    expect(input.style.fontFamily).toBe("cursive");
  });

  it("Check if the styles get applied with masked element for label [NEWPORT]", async () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "nds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    element.styles =
      '{"label":{"color":"#E4CC70","textAlign":"center","fontSize":"34px","textDecoration":"line-through","fontFamily":"cursive"},"value":{"color":"#E6EDE8","textAlign":"center","fontSize":"24px","backgroundColor":"#2A45DD","textDecoration":"line-through","fontFamily":"cursive"}}';

    document.body.appendChild(element);
    expect(element.maskedValue).toBe("+7(123)456-78-90");

    await delay(10);

    const label = element.shadowRoot.querySelector("label span");
    await delay(10);
    expect(label.style.color).toBe("rgb(228, 204, 112)"); // hex code: #E4CC70;
    expect(label.style.fontSize).toBe("34px");
    expect(label.style.textDecoration).toBe("line-through");
    expect(label.style.fontFamily).toBe("cursive");

    // For label textAlign doesn't support
    expect(label.style.textAlign).toBe("");
  });

  it("Check if the styles get applied with masked element for input value [NEWPORT]", async () => {
    const element = createElement("c-masked-input", {
      is: MaskedInput
    });
    let maskAttrib = {
      mask: "+{7}(000)000-00-00",
      lazy: false, // make placeholder always visible
      placeholderChar: "#" // defaults to '_'
    };
    element.label = "InputMask";
    element.theme = "nds";
    element.value = "1234567890";
    element.type = "text";
    element.imask = maskAttrib;
    element.styles =
      '{"label":{"color":"#E4CC70","textAlign":"center","fontSize":"34px","textDecoration":"line-through","fontFamily":"cursive"},"value":{"color":"#E6EDE8","textAlign":"center","fontSize":"24px","backgroundColor":"#2A45DD","textDecoration":"line-through","fontFamily":"cursive"}}';

    document.body.appendChild(element);
    expect(element.maskedValue).toBe("+7(123)456-78-90");

    await delay(10);

    const input = element.shadowRoot.querySelector(
      "input.vlocity-input.nds-input.nds-input_mask"
    );
    await delay(10);
    expect(input.style.color).toBe("rgb(230, 237, 232)"); // hex code: #E6EDE8;
    expect(input.style.fontSize).toBe("24px");
    expect(input.style.textAlign).toBe("center");
    expect(input.style.backgroundColor).toBe("rgb(42, 69, 221)"); // hex code: #2A45DD;
    expect(input.style.textDecoration).toBe("line-through");
    expect(input.style.fontFamily).toBe("cursive");
  });

  it("Check if role alert present on errormessageblock in slds theme", async () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "slds";
    element.type = "number";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    element.setCustomValidity("Error Message");
    expect(element.reportValidity()).toBe(false);
    return Promise.resolve().then(() => {
      const errorMessageBlock = element.shadowRoot.querySelector(
        ".slds-form-element__help"
      );
      expect(errorMessageBlock).not.toBeNull();
      expect(errorMessageBlock.role).toBe("alert");
    });
  });

  it("Check if role alert present on errormessageblock in newport theme", async () => {
    const element = createElement("c-masked-input", { is: MaskedInput });
    const maskAttrib = {
      mask: Number,
      numberMask: true
    };

    element.label = "InputMask";
    element.theme = "nds";
    element.type = "number";
    element.imask = maskAttrib;
    document.body.appendChild(element);
    element.setCustomValidity("Error Message");
    expect(element.reportValidity()).toBe(false);
    return Promise.resolve().then(() => {
      const errorMessageBlock = element.shadowRoot.querySelector(
        ".nds-form-element__help"
      );
      expect(errorMessageBlock).not.toBeNull();
      expect(errorMessageBlock.role).toBe("alert");
    });
  });
});
