import { createElement } from "lwc";
import Modal from "c/modal";
import pubsub from "c/pubsub";

describe("c-modal", () => {
  afterEach(() => {
    while (document.body.firstChild) {
      document.body.removeChild(document.body.firstChild);
    }
  });

  it("renders DOM elements", () => {
    const element = createElement("c-modal", { is: Modal });
    document.body.appendChild(element);
    element.openModal();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.children).not.toHaveLength(0);
    });
  });

  it("changes theme", () => {
    const element = createElement("c-modal", {
      is: Modal
    });
    element.title = "Modal";
    element.theme = "nds";
    document.body.appendChild(element);
    element.openModal();
    //console.log(element.shadowRoot.querySelector("section"));
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("section").className).toContain(
        "nds"
      );
      expect(
        element.shadowRoot.querySelector("section").className
      ).not.toContain("slds");
    });
  });

  it("opens modal", () => {
    const element = createElement("c-modal", {
      is: Modal
    });
    element.title = "Modal";
    document.body.appendChild(element);
    element.openModal();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("section")).not.toBe(null);
    });
  });
  it("registers pubsub  on modal", () => {
    const element = createElement("c-modal", {
      is: Modal
    });
    element.title = "Modal";
    element.channelName = "close:modal";
    document.body.appendChild(element);
    element.openModal();
    return Promise.resolve().then(() => {
      expect(element.shadowRoot.querySelector("div").style.display).toBe(
        "block"
      );
      pubsub.fire("close:modal", "close", "");
      return Promise.resolve().then(() => {
        expect(element.shadowRoot.querySelector("div").style.display).toBe(
          "none"
        );
      });
    });
  });
  it("test modal gets closed when escape key is pressed", () => {
    const element = createElement("c-modal", {
      is: Modal
    });
    element.title = "Modal";
    document.body.appendChild(element);
    element.openModal();
    return Promise.resolve().then(() => {
      const divContainer = element.shadowRoot.querySelector('[role="dialog"]');
      divContainer.dispatchEvent(
        new KeyboardEvent("keydown", {
          key: "Escape"
        })
      );
      return Promise.resolve().then(() => {
        expect(element.shadowRoot.querySelector("div").style.display).toBe(
          "none"
        );
      });
    });
  });
});
